import { useContext, useEffect, useState } from 'react'
import {DataContext} from "../../context.js"
import styles from "./Characters.module.css"

export default function Filter(){
    let [matchedCharacters, setMatchedCharacters] = useState([])
    let {characters, setCharacters, initialCharacters, character, setCharacter} = useContext(DataContext)

    const handleSubmit = e=>{
        e.preventDefault()
        if(character!==""){
            setMatchedCharacters([])
            fetch(`https://rickandmortyapi.com/api/character/?name=${character}`)
                .then(resp=>resp.json())
                .then(data=>{
                    if(data.results) setMatchedCharacters([...data.results])
                    setMatchedCharacters([])
                })
        }
        else setMatchedCharacters([...initialCharacters])
    }

    useEffect(()=>{
        if(matchedCharacters.length>0) setCharacters([...matchedCharacters])
    }, [matchedCharacters])

    console.log(characters)

    return(
        <>
            <form className={styles.character_form} onSubmit={handleSubmit}>
                <input className={styles.character_formInput} type="text" placeholder="Character" onChange={e=>setCharacter(e.target.value)}></input>
                <button type="submit">Submit</button>
            </form>
        </>
    )
}
