import { useState } from "react"
import styles from "./Login.module.css"

export default function Login(){
    let [name, setName] = useState("")
    let [password, setPassword] = useState("")
    let [isHiddenActive, setIsHiddenActive] = useState(true)
    const handleSubmitForm = e=>{
        e.preventDefault()

    }
    return(
        <div className={styles.login_container}>
            <form className={styles.login_form} onSubmit={handleSubmitForm}>
                <h1>USER LOGIN</h1>
                <input className={`input_primary ${styles.input}`} placeholder="Username..."></input>
                <div>
                    <input  className={`input_primary ${styles.input}`} type={isHiddenActive ? "password" : "text"} placeholder="Password..."></input>
                    <button className="" onClick={()=>setIsHiddenActive(prev=>!prev)}>Active</button>
                </div>
                <button className={`animated_btn ${styles.btn_submit}`} type="submit"><label>Login</label></button>
            </form>
        </div>
    )
}
