import { useState } from "react"
import styles from "./Login.module.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faXmark } from '@fortawesome/free-solid-svg-icons'
import {users_data} from "../../data/users_data.js"

export default function Login(){
    let [name, setName] = useState("")
    let [password, setPassword] = useState("")
    let [isHiddenActive, setIsHiddenActive] = useState(true)

    {/* hacer parte de errores */}
    const handleSubmitForm = e=>{
        e.preventDefault()
    }

    return(
        <div className={styles.login_container}>
            <h1 className={styles.h1}>USER LOGIN</h1>
            <form className={styles.login_form} onSubmit={handleSubmitForm}>
                <input className={`${styles.input} ${styles.input_name}`} placeholder="Username..."></input>
                <div className={styles.divPassword}>
                    <input  className={`${styles.input_password}`} onChange={e=>setName(e.target.value)} type={isHiddenActive ? "password" : "text"} placeholder="Password..."></input>
                    <button className={styles.btn_hide} onClick={()=>setIsHiddenActive(prev=>!prev)}>{<FontAwesomeIcon className={styles.hide_icon} icon={isHiddenActive ? faEye : faXmark}/>}</button>
                </div>
                <div className={styles.checkbox_container}>
                    <input onChange={e=>setName(e.target.value)} type="checkbox" name="" id="" />
                    <label>Remember me?</label>
                </div>
                <button className={`${styles.btn_submit}`} type="submit"><label>Login</label></button>
            </form>
        </div>
    )
}
