import Character from './Character';
import { useEffect, useContext } from 'react'
import {DataContext} from "../../context.js"
import Filter from "./Filter.jsx"
import Page from "./Page_number.jsx";
import './Characters.css'

export default function Cards({onClose}) {
   let {page, character, characters, setCharacters} = useContext(DataContext)

   useEffect(()=>{
      fetch(`https://rickandmortyapi.com/api/character?page=${page}`)
         .then(resp=>resp.json())
         .then(data=>setCharacters([...data]))
   }, [page])

   return(
      <>
         <div className="characters-container">
            <p className="subtitle">Characters</p>
            <Filter/>
            <div className="characters-grid">
               {characters.map((person, index)=><Character character={person} onClose={onClose} key={index}/>)}
            </div>
            {!character && <Page/>}
         </div>
      </>
   );
}
