import Character from './Character.jsx';
import { useEffect, useContext } from 'react'
import {DataContext} from "../../context.js"
import Filter from "./Filter.jsx"
import Page from "./Page_number.jsx";
import styles from "./Characters.module.css"

export default function CharactersList({onClose}) {
   let {page, character, characters, setCharacters} = useContext(DataContext)

   useEffect(()=>{
      fetch(`https://rickandmortyapi.com/api/character?page=${page}`)
         .then(resp=>resp.json())
         .then(data=>{
            setCharacters([...data.results])
         })
   }, [page])

   return(
      <div className={styles.characters_container}>
      <p className={styles.subtitle}>Characters</p>
         <div className={styles.characters_subcontainer}>
            <Filter/>
            <div className={styles.characters_grid}>
               {characters.map((person, index)=><Character character={person} onClose={onClose} key={index}/>)}
            </div>
            {!character && <Page/>}
         </div>
      </div>
   );
}
