import { useState } from "react";

export default function Card({character}) {
   let [firstEpisodeName, setFirstEpisodeName] = useState("") 
   let [statusColor, setStatusColor] = useState("")

   //getting first episode name
   let firstEpisodeURL  = character.episode[0]
   fetch(firstEpisodeURL)
      .then(resp=>resp.json())
      .then(data=>setFirstEpisodeName(data.name))

   //getting the status color
   switch(character.status){
      case "Alive": setStatusColor("green")
   }

   return (
      <div className="card-item">
         <img src={character.image} alt="" />
         <div className="card-info">
            <h2 className="card-name">{character.name}</h2>
            <div>
               <label className="card-status_color"></label>
               <label className="card-status">{character.status} - </label>
               <label className="card-species">{character.species}</label>
            </div>
            <div className="card-first_episode">
               <p>First seen in:</p>
               <p>{firstEpisodeName}</p>
            </div>
         </div>
      </div>
   );
}
