import Page from "./Page_number.jsx";
import { useContext, useEffect, useState } from 'react'
import {DataContext} from "../../context.js"
import './Characters.css';

export default function Filter(){
    let [matchedCharacters, setMatchedCharacters] = useState([])
    let {setCharacters, initialCharacters} = useContext(DataContext)

    useEffect(()=>{
        if(character!==""){
            setMatchedCharacters([])
            fetch(`https://rickandmortyapi.com/api/character/?name=${character}`)
                .then(resp=>resp.json())
                .then(data=>{
                    if(data.results) setMatchedCharacters([...data.results])
                })
        }
        else setMatchedCharacters([...initialCharacters])
    }, [character])

    useEffect(()=>{
        if(matchedCharacters.length>0) setCharacters([...matchedCharacters])
    }, [matchedCharacters])

    return(
        <>
            <div className="character-form">
                <input type="text" placeholder="Character" onChange={e=>setCharacter(e.target.value)}></input>
            </div>
        </>
    )
}
