import Card from './Card';
import { useContext } from 'react'
import {DataContext} from "../../context.js"

export default function Cards({onClose}) {
   let {characters} = useContext(DataContext)
   return(
      <div>{characters.map((person, index)=><Card character={person} onClose={onClose} key={index}/>)}
      </div>
   );
}
