import { useEffect, useState } from "react"
import {useParams} from "react-router-dom"
import styles from "./Characters.module.css"

export default function CharacterDetails(){
    let [character, setCharacter] = useState({})
    let {id} = useParams()
    useEffect(()=>{
        fetch(`https://rickandmortyapi.com/api/character/${id}`)
            .then(response=>response.json())
            .then(data=>setCharacter({...data}))
    }, [])
    return(
        <div className={styles.details_container}>
            <p className={styles.details_name}>{character?.name}</p>
            <div className={styles.card}>
                <img src={character?.image} alt="character img"></img>
                {/* pasar a una table */}
                <small className={styles.details_titles}>Status:</small><p>{character?.status}</p>
                <small className={styles.details_titles}>Specie:</small><p>{character?.species}</p>
                <small className={styles.details_titles}>Gender:</small><p>{character?.gender}</p>
                <small className={styles.details_titles}>Origin:</small><p>{character.origin?.name}</p>
                <small className={styles.details_titles}>Location:</small><p>{character.location?.name}</p>
            </div>
        </div>
    )
}
