import { useState } from "react"
import styles from "./Login.module.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faXmark } from '@fortawesome/free-solid-svg-icons'

export default function Login(){
    let [name, setName] = useState("")
    let [password, setPassword] = useState("")
    let [isHiddenActive, setIsHiddenActive] = useState(true)
    {/* hacer parte de errores */}
    const handleSubmitForm = e=>{
        e.preventDefault()
    }
    return(
        <div className={styles.login_container}>
            <form className={styles.login_form} onSubmit={handleSubmitForm}>
                <h1>USER LOGIN</h1>
                <input className={`input_primary ${styles.input}`} placeholder="Username..."></input>
                <div className={styles.divPassword}>
                    <input  className={`input_primary ${styles.input_password}`} type={isHiddenActive ? "password" : "text"} placeholder="Password..."></input>
                    <button className={styles.btn_hide} onClick={()=>setIsHiddenActive(prev=>!prev)}>{<FontAwesomeIcon icon={isHiddenActive ? faEye : faXmark}/>}</button>
                </div>
                <button className={`animated_btn ${styles.btn_submit}`} type="submit"><label>Login</label></button>
            </form>
        </div>
    )
}
