import { useEffect, useState } from "react"
import {useParams} from "react-router-dom"
import styles from "./Characters.module.css"

export default function CharacterDetails(){
    let [character, setCharacter] = useState({})
    let {id} = useParams()
    useEffect(()=>{
        fetch(`https://rickandmortyapi.com/api/character/${id}`)
            .then(response=>response.json())
            .then(data=>setCharacter({...data}))
    }, [])
    return(
        <div className={styles.details_container}>
            <div className={styles.cart}>
                <img src={character?.image} alt="character img"></img>
                <p className={styles.details_name}>{character?.name}</p>
                {/* <p><b className={styles.details_titles}>Status:</b> {character?.status}</p>
                <p><b className={styles.details_titles}>Specie:</b> {character?.species}</p>
                <p><b className={styles.details_titles}>Gender:</b> {character?.gender}</p>
                <p><b className={styles.details_titles}>Origin:</b> {character.origin?.name}</p>
                <p><b className={styles.details_titles}>Location:</b> {character.location?.name}</p> */}
            </div>
        </div>
    )
}
