import { useState } from "react"
import styles from "./Characters.module.css"

export default function FormAddCharacter(){
    //input states
    let [status, setStatus] = useState("")
    let [species, setSpecies] = useState("")
    let [origin, setOrigin] = useState("")
    let [location, setLocation] = useState("")

    //arrays to define the select options
    let arrSpecies = ['Human', 'Alien', 'Humanoid', 'Animal', 'Robot', 'Disease', 'Mythological Creature', 'Poopybutthole', 'unknown', 'Cronenberg'].sort((a,b)=>a.localeCompare(b))
    let arrStatus  = ['Alive', 'Dead', 'unknown']
    let arrOrigins = ['Earth (C-137)', 'Earth (Replacement Dimension)', 'Earth (K-83)', 'unknown']
    
    return(
        <form className={styles.character_form}>
            <p className={styles.character_filterTitle}>Add a character</p>
            <label className={styles.add_property}>Name:</label>
            <input  className={styles.add_input} type="text" placeholder="Example: Rick..."></input>

            <label className={styles.add_property}>Species:</label>
            <select className={styles.add_input} onChange={e=>setSpecies(e.target.value)}>
                <option value="">Select an option...</option>
                {arrSpecies.map(el=><option value={el}>{el}</option>)}
            </select>

            <label className={styles.add_property}>Status:</label>
            <select className={styles.add_input} onChange={e=>setStatus(e.target.value)}>
                <option value="">Select an option...</option>
                {arrSpecies.map(el=><option value={el}>{el}</option>)}
            </select>

            <label className={styles.add_property}>Origin:</label>
            <select className={styles.add_input} onChange={e=>setOrigin(e.target.value)}>
                <option value="">Select an option...</option>
                {arrOrigins.map(el=><option value={el}>{el}</option>)}
            </select>

            <label className={styles.add_property}>Location:</label>
            <select className={styles.add_input} onChange={e=>setLocation(e.target.value)}>
                <option value="">Select an option...</option>
                {arrOrigins.map(el=><option value={el}>{el}</option>)}
            </select>

            <button className={styles.character_submit} type="submit">Submit</button>
        </form>
    )
}
