export default function Card({character, onClose}) {
   return (
      <div className="card-item">
         <button onClick={()=>onClose()}>X</button>
         <img src={character.image} alt="" />
         <div>
            <h2>{character.name}</h2>
            <h2>{character.species}</h2>
            <h2>{character.gender}</h2>
         </div>
      </div>
   );
}
