import Card from './Card';
import { useContext } from 'react'
import {DataContext} from "../../context.js"
import './Cards.css'

export default function Cards({onClose}) {
   let {characters} = useContext(DataContext)
   return(
      <>
         <div className="cards-container">
            <div className="cards">
            {characters.map((person, index)=><Card character={person} onClose={onClose} key={index}/>)}
            </div>
         </div>
      </>
   );
}
