import Character from './Characters/Character.jsx';
import { useEffect, useContext } from 'react'
import {DataContext} from "../../context.js"
import FilterCharacter from "./Forms/FilterCharacter.jsx"
import FormAddCharacter from "./Forms/AddCharacter.jsx"
import AddButtons from './Forms/AddButtons.jsx';
import Page from "./Page_number/Page_number.jsx";
import styles from "./Characters.module.css"
import { useState } from 'react';

export default function CharactersList({onClose}) {
   let {page, character, characters, setCharacters} = useContext(DataContext)
   let [isActiveShowAll, setIsActiveShowAll] = useState(true)

   useEffect(()=>{
      fetch(`https://rickandmortyapi.com/api/character?page=${page}`)
         .then(resp=>resp.json())
         .then(data=>{
            setCharacters([...data.results])
         })
   }, [page])

   const handleShowCreatedChars = ()=> setIsActiveShowAll(false)

   let createdCharacters = localStorage.getItem('createdCharacters')

   return(
      <div className={styles.characters_container}>
         <p className={styles.subtitle}>{isActiveShowAll ? "Characters" : "Created Characters"}</p>
         <div className={styles.characters_subcontainer}>
            <div className={styles.characters_menu}>
               <FilterCharacter/>
               <FormAddCharacter/>
               <AddButtons onShowCreatedChars={handleShowCreatedChars} isActiveShowAll={isActiveShowAll}/>
            </div>
            <div className={styles.characters_grid}>
               {isActiveShowAll ? characters.map((person, index)=><Character character={person} onClose={onClose} key={index}/>)
               : createdCharacters.map((person, index)=><Character character={person} onClose={onClose} key={index}/>)}
            </div>
         </div>
         {!character && <Page/>}
      </div>
   );
}
